    .tunnel-adder h1 {
      color: @text;
    }

    .input {
      color: @text;
    }
    .input input {
      background-color: @crust;
      color: @subtext0;
      border-color: @accent-color;
    }

    .input select {
      background-color: @crust;
      color: @subtext0;
      border-color: @accent-color;
    }

    // Token Adding
    .token-adder {
      color: @text;
    }
    .token-adder select {
      background-color: @crust;
      color: @subtext0;
      border-color: @accent-color;
    }

    // Client Adding
    .client-adder {
      color: @text;
    }
    .client-adder select {
      background-color: @crust;
      color: @subtext0;
      border-color: @accent-color;
    }
    .client-adder input {
      background-color: @crust;
      color: @subtext0;
      border-color: @accent-color;
    }

    // User Adding
    .user-adder {
      color: @text;
    }
    .user-adder input {
      background-color: @crust;
      color: @subtext0;
      border-color: @accent-color;
    }
    // dialog
    .dialog__content {
      background-color: @crust;
      color: @text;
    }
  }
}
// vim:ft=less

