        background: @base;
      }
      background: @surface0;
    }

    .autocomplete-suggestions strong {
      color: @lavender;
    }

    .autocomplete-group strong {
      border-bottom-color: @text;
    }

    .button {
      background-color: @sapphire;
      border-color: @sapphire;
      color: @base;
    }

    button:hover,
    button:focus,
    .button:hover,
    .button:focus {
      background-color: darken(@sapphire, 25%);
      color: @base;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
      color: @text;
    }

    input[type="text"],
    input[type="password"],
    input[type="date"],
    input[type="datetime"],
    input[type="datetime-local"],
    input[type="month"],
    input[type="week"],
    input[type="email"],
    input[type="number"],
    input[type="search"],
    input[type="tel"],
    input[type="time"],
    input[type="url"],
    input[type="color"],
    textarea,
    input[type="file"],
    input[type="checkbox"],
    input[type="radio"],
    select,
    input[type="text"]:focus,
    input[type="password"]:focus,
    input[type="date"]:focus,
    input[type="datetime"]:focus,
    input[type="datetime-local"]:focus,
    input[type="month"]:focus,
    input[type="week"]:focus,
    input[type="email"]:focus,
    input[type="number"]:focus,
    input[type="search"]:focus,
    input[type="tel"]:focus,
    input[type="time"]:focus,
    input[type="url"]:focus,
    input[type="color"]:focus,
    textarea:focus,
    input[type="file"]:focus,
    input[type="checkbox"]:focus,
    input[type="radio"]:focus,
    select:focus {
      & when (@lookup ="latte") {
        background-color: @base;
        border-color: @surface0;
      }
      background-color: @surface0;
      border-color: @surface1;
      color: @text;
    }

    .header-nav a {
      color: @pink;
    }

    .header-nav a:hover {
      color: darken(@pink, 15%);
    }

    .jspTrack {
      & when (@lookup ="latte") {
        background: @surface0;
      }
      background: @surface1;
    }

    .f-dropdown {
      & when (@lookup ="latte") {
        background-color: @base;
      }
      background-color: @surface0;
      border-color: @subtext1;
    }

    .f-dropdown::before {
      border-color: transparent transparent @subtext1 transparent;
    }

    span.query-pick {
      color: @text;
    }

    .has-tip {
      color: @subtext1;
      border-color: @overlay1;
    }

    .has-tip:hover,
    .has-tip:focus {
      border-bottom-color: @sapphire;
      color: @sapphire;
    }

    label {
      color: @subtext1;
    }

    table {
      background: @base;
      & when (@lookup ="latte") {
        background-color: @surface0;
      }
      background-color: @surface1;
    }

    table tr th,
    table tr td {
      color: @subtext1;
    }

    .kanji-table td {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    table tr.even,
    table tr.alt,
    table tr:nth-of-type(2n) {
      & when (@lookup ="latte") {
        background: fadeout(@surface0, 0.5);
      }
      background: fadeout(@surface1, 0.5);
    }

    .reading-table tr ~ tr > td {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    table.kanji-match td {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    tr.match-row-kanji,
    tr.match-row-reading {
      & when (@lookup ="latte") {
        border-color: @surface0;
      }
      border-color: @surface1;
    }

    img {
      filter: contrast(1.2) opacity(85%);
    }

    /* Options */
    & when (@zen = 1) {
      .header,
      footer,
      .landing-page {
        display: none;
      }

      div.wrapper {
        display: flex;
        flex-direction: column;
        align-items: center;
        place-content: center center;
        padding: 50px 0;
        min-height: 100vh;
        margin: 0;
      }

      .gloss-all {
        width: 100%;
        padding: 0 10px;
      }

      #div-ichiran-result {
        padding: 0 10px;
      }

      #div-kanji-form,
      #w-form {
        width: 100%;
      }

      #k-form > div > div,
      #w-form > div > div {
        width: 100%;
        max-width: 304px;
      }
    }
  }
}
