        } // Not selected radio
        &[style*="background: rgb(219\, 219\, 219)"],
        &[style*="background: rgb(38\, 38\, 38)"],
        &[style*="background: rgb(54\, 54\, 54)"],
        &[style*="background: rgb(255\, 255\, 255)"] {
