      &[style*="color: rgb(250\, 250\, 250)"],
      &[style*="color: rgb(224\, 241\, 255)"],
      &[style*="color: rgb(0\, 55\, 107)"],
      &[style*="color: rgb(38\, 38\, 38)"] {
        color: @text !important;
      }

