    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accentColor: @catppuccin[@@lookup][@@accent];
    @linkColor: @accentColor;
    @linkHover: #lightenOrDarken(@accentColor, 5%) [ @result];

    #lightenOrDarken(@color, @value) {
      @result: if(
        @lookup = latte,
        darken(@color, @value),
        lighten(@color, @value)
      );
    }

    color-scheme: if(@lookup = latte, light, dark);
    background-color: @base !important;
    color: @text !important;

    ::placeholder {
      color: @subtext0;
    }

    &::-webkit-scrollbar {
      width: 0.25rem;
      background: transparent;
    }

    &::-webkit-scrollbar-thumb {
      border-radius: 9999px;
      background: @accentColor;
    }

    a,
    /* menu under the search bar */
    .pure-menu-heading {
      color: @linkColor;
      &:hover,
      &:focus,
      &:active {
        color: @linkHover !important;
      }
    }

    hr,
    /* legend borders are visible as dividers in Settings */
    legend {
      border-color: mix(@accentColor, @surface0, 50%);
      border-style: solid;
    }

    legend {
      color: @accentColor !important;
    }

    .pure-button-primary {
      background-color: @surface1 !important;
      color: @text !important;
      border-color: @crust !important;
      &:hover,
      &:focus {
        background-color: @surface2 !important;
        color: @text !important;
        border-color: @crust !important;
      }
    }
    .pure-button-secondary {
      background-color: @accentColor !important;
      color: @crust !important;
      border-color: @crust !important;
      &:hover,
      &:focus {
        background-color: #lightenOrDarken(@accentColor, 2%) [ @result] !important;
        color: @crust !important;
        border-color: @crust !important;
      }
    }

    #subscribe {
      background-color: @linkColor !important;
      color: @crust !important;
      :hover,
      :focus {
        background-color: @linkHover !important;
        color: @crust !important;
      }
    }

    #filters-box {
      background-color: @surface0 !important;
    }

    .underlined {
      border-color: @overlay1;
    }

