        filter: invert(1);
      }
      .cover-art {
        background: @base;
      }
      .avatar::after,
      .cover-art::after,
      .image-list-item::after,
      .video-thumbnail::after {
        box-shadow: inset 0 0 0 1px rgb(@subtext0, 7%);
      }
    }

    /* ------UNIVERSAL STYLES INCASE SOME GET MISSED------ */
    body {
      color: @text;
    }
    h1 {
      color: @accent;
    }
    .mimic-link {
      color: @sapphire;
    }
    /* -----------CHECKBOXES AND RADIOS---------- */
    .lfm-form-radio input[type="radio"]:checked + label::before {
      border-color: @accent;
