    }
    .recs-feed .recs-feed-cover-image-wrap::after {
      background-image: linear-gradient(
        180deg,
        transparent 0,
        rgba(@mantle, 0.4) 50%,
        rgba(@mantle, 0.7) 50%,
        rgba(@mantle, 0.7) 100%
      );
    }

    .recs-feed .context {
      background: @mantle;
      color: @text;
      box-shadow: none;
      box-shadow:
        inset 1px 0 darken(@mantle, 3%),
        inset -1px -1px @crust;
    }
    .recs-feed .recs-feed-item {
      background: transparent;
      box-shadow: none;
    }
    a {
