    }

    /* SEARCH */
    .artist-result-heading {
      color: @text;
    }

    .album-result-heading {
      color: @text;
    }

    /* RECOMMENDATIONS */
    & when not (@lookup = "latte") {
      .recs-feed-title a {
        color: @text !important;
      }
    }
    & when (@lookup = "latte") {
