      border-radius: 4px;
    }
    textarea:focus,
    select:focus,
    input[type="submit"]:focus {
      border-color: @accent;
      outline-color: @overlay0;
    }
    input[type="submit"]:hover {
      background-color: @mantle;
    }

    /* Syntax-highlighted code */
    pre {
      background-color: @base !important;
    }
    /* Main text */
    span[style="color:#657b83;"] {
      color: @text !important;
    }
    span[style="color:#859900;"] {
      color: @yellow !important;
    }
    span[style="color:#268bd2;"] {
      color: @sky !important;
    }
    /* Comments */
    span[style="color:#93a1a1;"] {
      color: @overlay1 !important;
    }
    span[style="color:#b58900;"] {
      color: @yellow !important;
    }
    span[style="color:#586e75;"] {
      color: @mauve !important;
    }
    /* Quotes */
    span[style="color:#839496;"] {
      color: @green !important;
    }
    /* String literals */
    span[style="color:#2aa198;"] {
      color: @green !important;
    }
    span[style="color:#6c71c4;"] {
      color: @teal !important;
    }
    /* self/this */
    span[style="color:#d33682;"] {
      color: @red !important;
    }
    /* Constants */
    span[style="color:#cb4b16;"] {
      color: @peach !important;
    }
    /* String escape sequences */
    span[style="color:#dc322f;"] {
      color: @maroon !important;
    }
  }
}
